package Array6;/** * No name */public class AnonymousArray {    public static void showArray(int[] array){        for(final int x: array){            System.out.print(x+" ");        }        System.out.println();    }    public static void main(String[] args) {        showArray(new int[]{1, 2, 3,4, 5});        System.out.println("Sum of them: "+sumElementsOfAnArray(new double[]{1,2,3,4,5,6,7,8,9}));    }    public static double sumElementsOfAnArray(double[] array){        double sum = 0.0;        for(final double x: array){            sum += x;        }        return sum;    }}