package Array6;/** * Counting frequencies of 100 lowercase letters */public class CountFrequencyCharArray {    /*General from of random things*/    public static char getRandomCharacter(char ch1, char ch2){        return (char) (ch1 + Math.random() * (ch2 - ch1 + 1));    }    /*Generate a random lowercase letter*/    public static char getRandomLowerCaseLetter(){        return getRandomCharacter('a', 'z');    }    public static char[] createArray(int arraySize){      final char[] lowerCasedChars = new char[arraySize];        for(int i = 0; i < arraySize; i++){            lowerCasedChars[i] = getRandomLowerCaseLetter();        }        return lowerCasedChars;    }    public static void main(String[] args) {        final char[] lowerCasedChars = createArray(100);        showCharacters(lowerCasedChars);        System.out.println();        final int[] frequencies = getCountingFrequencies(lowerCasedChars);        showFrequencies(frequencies);    }    public static int[] getCountingFrequencies(char[] chars){        final int[] frequencies = new int[26];        for(final char ch : chars){            frequencies[ch - 'a']++;        }        return frequencies;    }    public static void showFrequencies(int[] frequencies){        System.out.println("The occurrence of each letter are:");        final int len = frequencies.length;        for(char i = 0; i < len; i++){            System.out.print((char)('a'+i)+" ");        }        System.out.println();        for(final int frequency : frequencies){            System.out.print(frequency+" ");        }        System.out.println();    }    public static void showCharacters(char[] chars){        System.out.println("The lower case letter are:");        int count = 0;        for(final char ch: chars){            count++;            System.out.print(ch+" ");            if(count % 20 == 0){                System.out.println();            }        }    }}