package Array6;public class DeleteAnElementFromArray {    public static boolean linearSearch(int[] array, int key){        for(final int x: array){            if(x == key){                return true;            }        }        return false;    }    public static int[] createAnArray(int size){        final int[] array = new int[size];        int element;        for(int i = 0; i < size; i++){            do {                element = (int) (Math.random()*size+1);            }while(linearSearch(array, element));            array[i] = element;        }        return array;    }    public static void main(String[] args) {        final int size = 10;        final int[] array = createAnArray(size);        showArrayElements(array, size);        int position = 1;        if(isDeleteAnElementFromArray(array, size, position)){            showArrayElements(array, size - 1);        }        else{            System.out.println("Deletion NOT possible!");        }    }    public static boolean isDeleteAnElementFromArray(int[] array, int size, int position){        if(position > size || position < 1){            return false;        }        else{           /*               Index = position - 1               One element remove so, size - 1            */            for(int i = position - 1; i < size-1; i++){                array[i] = array[i+1];            }            return true;        }    }    public static void showArrayElements(int[] array, int size){        for (int i = 0; i < size; i++){            System.out.print(array[i]+" ");        }        System.out.println();    }}