package Array6;/*** * Count frequency of a random array list */public class FrequencyNumber {    public static int[] getRandomList(int listSize){        int[] randomList = new int[listSize];        for(int i = 0; i < listSize; i++){            randomList[i] = (int) (Math.random() * listSize);        }        return randomList;    }    public static int[] getFrequenciesOfList(int[] randomList){        int[] frequencies = new int[10];        for(final int number: randomList){            frequencies[number]++;        }        return  frequencies;    }    public static void main(String[] args) {        int[] randomList = getRandomList(10);        int[] frequencies = getFrequenciesOfList(randomList);        showFrequencyZeroToTen(frequencies);        showRandomList(randomList);    }    public static void showFrequencyZeroToTen(int[] frequencies){        for(int i = 0; i < 10; i++){            System.out.print(i +" ");        }        System.out.println();        for(final int frequency : frequencies){            System.out.print(frequency+" ");        }        System.out.println();    }    public static void showRandomList(int[] randomList){        System.out.println("Elements are: ");        for(final int number : randomList){            System.out.print(number+" ");        }        System.out.println();    }}