package Array6;public class RaggedArray2 {    public static void loadElementsForArray(int[] array){        final int len = array.length;        for(int i = 0; i < len; i++){            array[i] = i+1;        }    }    public static void main(String[] args) {        int[][] triangle = new int[5][];        /*First row's 5 columns*/        triangle[0] = new int[5];        loadElementsForArray(triangle[0]);        /*Second row's 4 columns*/        triangle[1] = new int[4];        loadElementsForArray(triangle[1]);        /*Third row's 3 columns*/        triangle[2] = new int[3];        loadElementsForArray(triangle[2]);        /*Fourth row's 2 columns*/        triangle[3] = new int[2];        loadElementsForArray(triangle[3]);        /*Fifth row's columns*/        triangle[4] = new int[1];        loadElementsForArray(triangle[4]);        /*Print using enhance for loop*/        for(int[] x: triangle){            for(int y: x){                System.out.printf("%4d", y);            }            System.out.println();        }    }}