package Array6;/** * Array support pass by reference in Java, so array is reference typed data type */public class StringArrayPassInMethod {    public static String[] createNamesArray(int size){        final String[] namesArray = new String[size];        namesArray[0] = "Muhammad Yousuf Ali";        namesArray[1] = "Imtaiz Ahmad Maruf";        namesArray[2] = "Tofayel Ahmad Tamim";        return namesArray;    }    public static void stringPassByReference(String[] array){        array[0] = "Yousuf";        array[1] = "Maruf";        array[2] = "Tamim";    }    public static void main(String[] args) {        final String[] namesArray = createNamesArray(3);        for(final String name: namesArray){            System.out.println(name);        }        stringPassByReference(namesArray);        System.out.println("------------------------------");        for(final String name: namesArray){            System.out.println(name);        }    }}